function gadget:GetInfo()  return {    name      = "Peace Spawner",    desc      = "Like war, but peaceful.",    author    = "Google Frog, modified by Anarchid",    date      = "14 May 2013",    license   = "GNU GPL, v2 or later",    layer     = 0,    enabled   = true  --  loaded by default?  }end--------------------------------------------------------------------------------------------------------------------------------------------------------------------------if (not gadgetHandler:IsSyncedCode()) then    returnend--------------------------------------------------------------------------------------------------------------------------------------------------------------------------local MAP_SIZE_X = Game.mapSizeXlocal MAP_SIZE_Z = Game.mapSizeZlocal CMD_FIGHT = CMD.FIGHTlocal spawnDef = UnitDefNames["armpw"].idlocal singuDef = UnitDefNames["cafus"].idlocal SPACING = 200local LineLength = 200local spawnCount = 100local END_FRAME = 30*32 -- should be around 30 secondslocal spawn = {	[0] = {x = MAP_SIZE_X*0.5 - LineLength*SPACING*0.5, z = MAP_SIZE_Z*0.5-500, face = 0, pos = 0},	[1] = {x = MAP_SIZE_X*0.5 - LineLength*SPACING*0.5, z = MAP_SIZE_Z*0.5+500, face = 2, pos = 0}	}function gadget:GameStart() -- GameStart	Spring.Echo("Peace Spawner Active")	for i = 1, spawnCount do		for t = 0, 1 do			spawn[t].pos = spawn[t].pos + 1			if spawn[t].pos > LineLength then				spawn[t].pos = 0			end						local unitID = Spring.CreateUnit(spawnDef, spawn[t].x + spawn[t].pos*SPACING, 0, spawn[t].z, spawn[t].face, t)			--units[t][#units[t] + 1] = unitID		end	end		local singu = Spring.CreateUnit(singuDef, MAP_SIZE_X*0.5, 0, MAP_SIZE_Z*0.5,0,0);	Spring.DestroyUnit(singu);endfunction gadget:GameFrame(f)	if f == END_FRAME then		Spring.GameOver({})	endend