function gadget:GetInfo()  return {    name      = "War Spawner",    desc      = "Spawns units for war test. Use with NullAI",    author    = "Google Frog",    date      = "14 May 2013",    license   = "GNU GPL, v2 or later",    layer     = 0,    enabled   = true  --  loaded by default?  }end--------------------------------------------------------------------------------------------------------------------------------------------------------------------------if (not gadgetHandler:IsSyncedCode()) then    returnend--------------------------------------------------------------------------------------------------------------------------------------------------------------------------local MAP_SIZE_X = Game.mapSizeXlocal MAP_SIZE_Z = Game.mapSizeZlocal CMD_FIGHT = CMD.FIGHTlocal spawnDef = UnitDefNames["corraid"].idlocal SPACING = 90local rows = 2local LineLength = 100local spawnCount = 100local END_FRAME = 610local spawn = {	[0] = {x = MAP_SIZE_X*0.5 - LineLength*SPACING*0.5, z = MAP_SIZE_Z*0.65-100, extraRow = -50, face = 0, pos = 0},	[1] = {x = MAP_SIZE_X*0.5 - LineLength*SPACING*0.5, z = MAP_SIZE_Z*0.65+100, extraRow = 50, face = 2, pos = 0}	}function gadget:GameStart() -- GameStart	Spring.Echo("War Spawner Active")	for i = 1, spawnCount do		for r = 0, rows-1 do			for t = 0, 1 do				spawn[t].pos = spawn[t].pos + 1				if spawn[t].pos > LineLength then					spawn[t].pos = 0				end								local unitID = Spring.CreateUnit(spawnDef, 					spawn[t].x + spawn[t].pos*SPACING, 					0, 					spawn[t].z, spawn[t].face + spawn[t].extraRow*r, t)				Spring.GiveOrderToUnit(unitID, CMD_FIGHT, {spawn[1 - t].x  + spawn[t].pos*SPACING,0,spawn[1 - t].z}, {})				--units[t][#units[t] + 1] = unitID			end		end	end	endfunction gadget:GameFrame(f)	if f == END_FRAME then		Spring.GameOver({})	endend